function(string $accessToken, string $host = '')
  {
    if (empty($accessToken)) {
      throw new \Error(_('The access token is mandatory'));
    }

    $this->token = $accessToken;
    $host        = !empty(\trim($host)) ? \trim($host) : 'localhost';
    $this->host  = $host . (\str_ends_with($host, '/') ? '' : '/') . 'api/v4/';
  }

getCommits.code:
function($project, string $branch = '', string $filePath = '', string $since = '', string $until = '')
  {
    $params = [];
    if (!empty($filePath)) {
      $params['path'] = \urldecode($filePath);
    }
    if (!empty($branch)) {
      $params['branch'] = $branch;
    }
    if (!empty($since)) {
      $params['since'] = \date('c', \strtotime($since));
    }
    if (!empty($until)) {
      $params['until'] = \date('c', \strtotime($until));
    }
    return $this->request($this->projectURL . $project . '/repository/commits', $params);
  }

getDiff.code:
function($project, string $id, string $filePath = '')
  {
    $diff = $this->request($this->projectURL . $project . '/repository/commits/' . $id . '/diff');
    if (!empty($filePath)) {
      if (!\is_null($i = \bbn\X::find($diff, function($d) use($filePath){
        return ($d->old_path === $filePath) || ($d->new_path === $filePath);
      }))) {
        return \bbn\X::toArray($diff[$i]);
      }
      return [];
    }
    return $diff;
  }

createIssue.code:
function($project, string $title, string $description = '', array $labels = [], int $assigned = NULL, bool $private = false, string $date = '')
  {
    string $title,
    string $description = '',
    array $labels = [],
    int $assigned = null,
    bool $private = false,
    string $date = ''
  ): ?array
  {
    $params = [
      'title' => $title,
      'description' => $description,
      'labels' => \implode(',', $labels),
      'issue_type' => 'issue'
    ];
    if (!empty($private)) {
      $params['confidential'] = 'true';
    }
    if (!empty($assigned)) {
      $params['assignee_ids'] = $assigned;
    }
    if (!empty($date)) {
      $params['created_at'] = \date('c', \strtotime($date));
    }
    if ($issue = $this->post($this->projectURL . $project . '/' . $this->issueURL, $params)) {
      return $issue;
    }
    return null;
  }

editIssue.code:
function($project, int $iid, string $title, string $description = '', array $labels = [], int $assigned = 0, bool $private = false)
  {
    int $iid,
    string $title,
    string $description = '',
    array $labels = [],
    int $assigned = 0,
    bool $private = false
  ): array
  {
    $params = [
      'title' => $title,
      'description' => $description,
      'labels' => \implode(',', $labels),
      'confidential' => empty($private) ? 'false' : 'true',
      'assignee_ids' => $assigned
    ];
    return $this->put($this->projectURL . $project . '/' . $this->issueURL . $iid, $params);
  }

