getCommits.code:
function($project, string $branch = '', string $filePath = '', string $since = '', string $until = '')
  {
    $params = [];
    if (!empty($filePath)) {
      $params['path'] = \urldecode($filePath);
    }
    if (!empty($branch)) {
      $params['branch'] = $branch;
    }
    if (!empty($since)) {
      $params['since'] = \date('c', \strtotime($since));
    }
    if (!empty($until)) {
      $params['until'] = \date('c', \strtotime($until));
    }
    return $this->request($this->projectURL . $project . '/repository/commits', $params);
  }

getDiff.code:
function($project, string $id, string $filePath = '')
  {
    $diff = $this->request($this->projectURL . $project . '/repository/commits/' . $id . '/diff');
    if (!empty($filePath)) {
      if (!\is_null($i = \bbn\X::find($diff, function($d) use($filePath){
        return ($d->old_path === $filePath) || ($d->new_path === $filePath);
      }))) {
        return \bbn\X::toArray($diff[$i]);
      }
      return [];
    }
    return $diff;
  }

createIssue.code:
function($project, string $title, string $date = '')
  {
    $params = [
      'title' => \urlencode($title)
    ];
    if (!empty($date)) {
      $params['created_at'] = \date('c', \strtotime($date));
    }
    if ($issue = $this->post($this->projectURL . $project . '/' . $this->issueURL, $params)) {
      return $issue['id'];
    }
    return null;
  }

createIssueNote.code:
function($project, int $issue, string $content, string $date = '')
  {
    $params = [
      'body' => \urlencode($content)
    ];
    if (!empty($date)) {
      $params['created_at'] = \date('c', \strtotime($date));
    }
    if (($iss = $this->getIssue($issue))
      && !empty($iss['iid'])
      && ($note = $this->post($this->projectURL . $project . '/' . $this->issueURL . $iss['iid'], $params))
    ) {
      return $note['id'];
    }
    return null;
  }
