public function copy(string $source, string $dest): bool
{

  if ( $this->exists($source) ){

    if ( !empty($dest) ){
      if ( !$this->exists($dest) ){
        return (bool)$this->obj->request('COPY', $source, null, [
          'Destination' => self::prefix.$dest
        ]);
      }
      else {
        $this->error = X::_("The given destination already exists");
        return false;
      }
    }
  }
}

