listMainAccountPayments.code:
function($api_key, array $params = [], $from = NULL, $limit = 250)
  {
    array $params = [],
    string $from = null,
    int $limit = 250
  ): array
  {
    $mollie = new MollieApiClient();
    $mollie->setApiKey($api_key);

    $payments = $mollie->payments->page($from, $limit, $params);

    return X::toArray($payments);
  }

getMainAccountPayment.code:
function($api_key, $payment_id, array $params = [])
  {
    $mollie = new MollieApiClient();
    $mollie->setApiKey($api_key);

    $payment = $mollie->payments->get($payment_id, $params);

    return X::toArray($payment);
  }
