__construct.code:
public function __construct(string $accessCode, string $accessMode = 'token')
{
  $this->mollie = new MollieApiClient();
  switch ($accessMode) {
    case 'token':
      $this->mollie->setAccessToken($accessCode);
      break;
    case 'key':
      $this->mollie->setApiKey($accessCode);
      break;
    default:
      $this->mollie->setAccessToken($accessCode);
      break;
  }
}

createPayment.code:
public function createPayment(array $data, ?string $customer_id = null, ?string $mandate_id = null): array
{
  if ($customer_id) {
    $data = array_merge($data, ['customerId' => $customer_id]);
  }

  if ($mandate_id) {
    $data = array_merge($data, ['mandateId' => $mandate_id]);
  }

  $payment = $this->mollie->payments->create($data);
  return X::toArray($payment);
}

createPaymentFirstTime.code:
public function createPaymentFirstTime($customer, array $payment_data): array
{
  $customer = \is_array($customer) ? $this->mollie->customers->create($customer)->id : $customer;
  $payment_data = array_merge($payment_data, ['sequenceType' => 'first']);
  try {
    return $this->createPayment($payment_data, $customer);
  }
  catch (\Exception $e) {
    throw new \Exception($e->getMessage());
  }
}

createOnDemandPayment.code:
public function createOnDemandPayment(array $payment_data, string $customer_id, string $mandate_id): ?array
{
  if (!$this->customerHasValidMandate(
    $customer_id,
    $mandate_id,
    array_key_exists('testmode', $payment_data) ? ['testmode' => $payment_data['testmode']] : [])
  ) {
    // If the mandate is not valid then creates a new one by having
    // The customer performs a first payment: createPaymentFirstTime()
    // Then update the new customer and mandate id in database.
    return null;
  }
  $payment_data = array_merge($payment_data, ['sequenceType' => 'recurring']);
  return $this->createPayment($payment_data, $customer_id, $mandate_id);
}

customerHasValidMandate.code:
private function customerHasValidMandate(string $customer_id, string $mandate_id, array $params = []): bool
{
  $mandate = $this->getMandate($customer_id, $mandate_id, $params);

  return $mandate['status'] === 'valid';
}

