updateProfile.code:
public function updateProfile(string $idProfile, array $data): array
{
  $profile = $this->mollie->profiles->get($idProfile);
  foreach ($data as $k => $d) {
    $profile->{$k} = $d;
  }
  $updatedProfile = $profile->update();
  return X::toArray($updatedProfile);
}

