__construct.code:
function(string $accessCode, string $accessMode = 'token')
  {
    $this->mollie = new MollieApiClient();
    switch ($accessMode) {
      case 'token':
        $this->mollie->setAccessToken($accessCode);
        break;
      case 'key':
        $this->mollie->setApiKey($accessCode);
        break;
      default:
        $this->mollie->setAccessToken($accessCode);
        break;
    }
  }

createPayment.code:
function(array $data, string $customer_id = NULL, string $mandate_id = NULL)
  {
    if ($customer_id) {
      $data = array_merge($data, ['customerId' => $customer_id]);
    }

    if ($mandate_id) {
      $data = array_merge($data, ['mandateId' => $mandate_id]);
    }

    $payment = $this->mollie->payments->create($data);
    return X::toArray($payment);
  }

createPaymentFirstTime.code:
function($customer, array $payment_data)
  {
    $customer = \is_array($customer) ? $this->mollie->customers->create($customer)->id : $customer;
    $payment_data = array_merge($payment_data, ['sequenceType' => 'first']);
    try {
      return $this->createPayment($payment_data, $customer);
    }
    catch (\Exception $e) {
      throw new \Exception($e->getMessage());
    }
  }

createOnDemandPayment.code:
function(array $payment_data, string $customer_id, string $mandate_id)
  {
    if (!$this->customerHasValidMandate(
      $customer_id,
      $mandate_id,
      array_key_exists('testmode', $payment_data) ? ['testmode' => $payment_data['testmode']] : [])
    ) {
      // If the mandate is not valid then creates a new one by having
      // The customer performs a first payment: createPaymentFirstTime()
      // Then update the new customer and mandate id in database.
      return null;
    }
    $payment_data = array_merge($payment_data, ['sequenceType' => 'recurring']);
    return $this->createPayment($payment_data, $customer_id, $mandate_id);
  }

refundPayment.code:
function(string $payment_id, string $amount, array $params = [])
  {
    $payment = $this->mollie->payments->get($payment_id, ['testmode' => !empty($params['testmode'])]);
    $params = array_merge([
      "amount" => [
        "currency" => $payment->amount->currency,
        "value"    => $amount
      ]
    ], $params);
    $refund = $payment->refund($params);
    return X::toArray($refund);
  }

updateProfile.code:
function(string $idProfile, array $data)
  {
    $profile = $this->mollie->profiles->get($idProfile);
    foreach ($data as $k => $d) {
      $profile->{$k} = $d;
    }
    $updatedProfile = $profile->update();
    return X::toArray($updatedProfile);
  }

listPayments.description:
This will list all payments not only the paid ones
so when looping through results you can check if a payment is paid:

```php
$client_payments = $mollie_manager->listPayments([
 'testmode' => true,
]);
foreach ($client_payments as $payment) {
 if (!empty($payment['paidAt'])) {
   echo "ID: $payment['id'] <br>";
   echo "Amount: {$payment['amount']['value']} {$payment['amount']['currency']} <br>";
   echo "Status: {$payment['status']} <br><br>";
 }
}
```

