public function deleteCache($command_name = '', $arguments = false)
{
  $uid = $this->hostname;
  if (!empty($arguments)) {
    $uid .= md5(json_encode($arguments));
  }

  if (!empty($this->cacheDelete($uid, $command_name))) {
    X::log([$uid, $command_name], 'cache_delete');
    return true;
  }

  return false;
}

