protected function cacheInit(): void
{
  if ( \is_null($this->cache_engine) ){
    $this->cache_engine = CacheCls::getEngine();
    $this->_cache_prefix = Str::encodeFilename(str_replace('\\', '/', \get_class($this)), true).'/';
  }
}

