private function processParameters(array $param)
{
  foreach ($param as $key => $val) {
    if (\is_array($val)) {
      $param[$key] = $this->processParameters($val);
    }
    else {
      $param[$key] = $this->sanitize($val);
    }
  }

  return $param;
}

