public function __construct(array $cfg)
{
  if (empty($cfg['user'])) {
    throw Error(_('The username is mandatory'));
  }

  if (empty($cfg['pass'])) {
    throw Error(_('The password is mandatory'));
  }

  $this->cacheInit();
  $this->user     = $cfg['user'];
  $this->pass     = $cfg['pass'];
  $this->hostname = isset($cfg['host']) ? $cfg['host'] : 'localhost';
}

