setConfig.code:
function(array $cfg = NULL, bbn\File\System $fs = NULL)
  {
    $this->unsetConfig();
    $this->_currentFs = $fs ?? new System();
    $has_cfg          = (bool)$cfg;
    foreach (self::$vars as $v) {
      if ($has_cfg) {
        $this->_current[$v] = $cfg[$v] ?? null;
      }
      elseif (defined('\\BBN_'.strtoupper($v))) {
        $this->_current[$v] = constant('\\BBN_'.strtoupper($v));
      }
    }

    $c =& $this->_current;

    $url = 'http';
    if ($c['is_ssl']) {
      $url .= 's';
    }

    $url .= '://' . $c['server_name'];
    if (isset($c['port']) && !in_array($c['port'], [80, 443])) {
      $url .= ':'.$c['port'];
    }

    if (!empty($c['cur_path'])) {
      $url .= $c['cur_path'];
    }

    if (substr($url, -1) !== '/') {
      $url .= '/';
    }

    $this->_current['url'] = $url;
  }

