public function saveConversation(
  string $path,
  string $date,
  string $userFormat,
  string $prompt,
  ?string $response,
  ?array $cfg = null
): array {
  $timestamp = time();
  $fs = new System();
  if ($fs->exists($path)) {
    $jsonData = $fs->decodeContents($path, 'json', true);

  } else {
    $jsonData = [];
  }

  $d = [[
    "ai" => 0,
    "creation_date" => $date,
    "text" => $prompt,
    'id' => bin2hex(random_bytes(10)),
    'format' => $userFormat ?? 'textarea'
  ], [
    "ai" => 1,
    "creation_date" => $timestamp,
    "text" => $response,
    'id' => bin2hex(random_bytes(10)),
  ]];

  if ($cfg) {
    $d[1]['cfg'] = $cfg;
  }
  if (isset($jsonData['conversation'])) {
    array_push($jsonData['conversation'], ...$d);
  } else {
    array_push($jsonData, ...$d);
  }

  $fs->encodeContents($jsonData, $path, 'json');
  return $d;
}

