{
  if ($this->dbTraitExists($filter)) {
    $cfg = $this->getClassCfg();
    $f = $cfg['arch'][$this->class_table_index];

    if (!is_array($filter) && !empty($f['id'])) {
      $filter = [$f['id'] => $filter];
