public function __construct(User $user, Db $db, int $ttl = 300)
{
  if ($user->getId()) {
    $this->db = $db;
    $this->jwt = new Jwt();
    $this->jwt->prepare($user->getId(), $user->getOsession('fingerprint'), $ttl);
  }
}

