      'fields' => ['id_chat'],
      'join' => [[
        'table' => 'bbn_chats',
        'on' => [
          'conditions' => [[
            'field' => 'bbn_chats_users.id_chat',
            'exp' => 'bbn_chats.id'
          ]]
        ]
      ]],
      'where' => $where,
      'order' => [[
        'field' => 'bbn_chats.last_message',
        'dir' => 'DESC'
      ]]
      ]
    );
  }
}

