isPublished.code:
function(string $id_note)
  {
    $now = strtotime(date('Y-m-d H:i:s'));
    $cfg = $this->class_cfg;

    if ($event = $this->getEvent($id_note)) {
      if (
            isset($event[$cfg['arch']['events']['start']]) &&
            (is_null($event[$cfg['arch']['events']['end']]) || (strtotime($event[$cfg['arch']['events']['end']]) > $now)) &&
