function($id_note)
  {
		if ($id_event = $this->_notes->getEventIdFromNote($id_note)){
			if ($event = $this->db->rselect([
						'table' => $this->class_cfg['table'], 
						'fields' => [],
						'where' => [
							'conditions' => [[
								'field' => $this->fields['id'],
								'value' => $id_event
							]]], 
						]
