private function _check_date(?string $start, ?string $end): bool
{
  if (!isset($start)) {
    return false;
  }

  $start = strtotime($start);
  if (!$start) {
    throw new Exception(X::_("The end date is not valid"));
  }

  if (empty($end)) {
    return true;
  }

  $end = strtotime($end);
  if (!$end) {
    throw new Exception(X::_("The end date is not valid"));
  }

  if ($end <= $start) {
    //throw new Exception(X::_("The end date is before the start"));
    return false;
  }

  return true;
}

