private function normalizeItemPublications(array $item): array
{
  $itemType = $item['noteType'];
  if (!empty($item['noteType'])
    && ($item['noteType'] === 'news')
  ) {
    $itemType = null;
    $filters = [
      'conditions' => [[
        'field' => 'start',
        'operator' => '<=',
        'value' => date('Y-m-d H:i:s')
      ], [
        'field' => 'id_type',
        'operator' => '!=',
        'value' => $this->opt->fromCode('pages', 'types', 'note', 'appui'),
      ], [
        'logic' => 'OR',
        'conditions' => [[
          'field' => 'end',
          'operator' => '>=',
          'value' => date('Y-m-d H:i:s')
        ], [
          'field' => 'end',
          'operator' => 'isnull'
        ]]
      ]]
    ];
  }

  if (!empty($item['content'])) {
    $filters['conditions'][] = [
      'field' => 'id_option',
      'value' => $item['content']
    ];
  }

  $item['items'] = [[
    'component' => "appui-note-cms-block-slider-slide",
    'data' => array_map(
      function($a) {
        $a['component'] = "appui-note-cms-block-slider-slide";
        $a['type'] = 'img';
        $a['content'] = $a['front_img']['url'];
        return $a;
      },
      $this->getAll(
        false,
        $filters,
        [$item['order'] => 'desc'],
        $item['limit'],
        0,
        $itemType
      )['data']
    )
  ]];

  return $item;
}

