protected function setCache($data)
{
  $max = 600;
  if (isset($data['time'])) {
    if ($data['time'] < 0.01) {
      $ttl = 3;
    } else if ($data['time'] < 0.1) {
      $ttl = 10;
    } else if ($data['time'] < 0.5) {
      $ttl = 30;
    } else {
      $ttl = ceil($data['time'] * 60);
    }
    if ($ttl > $max) {
      $ttl = $max;
    }
    $this->cacheSet($this->cache_uid, '', $data, $ttl);
  }
  return $this;
}

