public function getQuery(): ?string
{
  if ($this->db->check()) {
    if ($this->query) {
      return $this->query . PHP_EOL .
        $this->db->getWhere($this->cfg) .
        $this->db->getGroupBy($this->cfg) .
        $this->db->getOrder($this->cfg) .
        $this->db->getLimit($this->cfg);
    }
    return $this->cfg['sql'];
  }
  return null;
}

