public function getData(): ?array
{
  if ($this->check()) {
    $this->chrono->start();
    if ($this->query) {
      $this->sql = $this->getQuery();
      $q = $this->db->query(
        $this->sql,
        \array_map(
          function ($v) {
            if (Str::isUid($v)) {
              $v = hex2bin($v);
            }
            return $v;
          },
          $this->db->getQueryValues($this->cfg)
        )
      );

      $rows = $q ? $q->getRows() : null;
    } else {
      $rows = $this->db->rselectAll($this->cfg);
      $this->sql = $this->cfg['sql'];
    }

    $this->query_time = $this->chrono->measure();
    $this->chrono->stop();
    return $rows;
  }

  return null;
}

