public function toExcel(array|null $data = null, array|null $options = null): array
{
  $path = X::makeStoragePath(\bbn\Mvc::getUserTmpPath()) . 'export_' . date('d-m-Y_H-i-s') . '.xlsx';
  $cfg = $this->getExcel();
  $dates = array_values(
    array_filter(
      $cfg['fields'],
      fn($c) => empty($c['hidden'])
        && (($c['type'] === 'date')
          || ($c['type'] === 'datetime')
          || ($c['type'] === 'isodate'))
    )
  );
  $data = array_map(function ($row) use ($dates, $options) {
    foreach ($row as $i => &$r) {
      if ($options && isset($options[$i])) {
        $r = X::getField($options[$i], ['value' => $row[$i]], 'text');
      }

      if (\is_string($r)) {
        $r = strpos($r, '=') === 0 ? ' ' . $r : $r;
      }
      if ((($k = X::search($dates, ['field' => $i])) !== null)) {
        if (!empty($dates[$k]['format']) && !empty($r)) {
          $r = date($dates[$k]['format'], strtotime($r));
        }
      }
    }

    unset($r);
    return $row;
  }, $data ?: ($this->getData() ?: []));
  if (X::toExcel($data, $path, true, $cfg)) {
    return ['file' => $path];
  }
  return ['error' => X::_('Error')];
}

