  private function decode_encoded_words_deep($obj) {
    if (is_string($obj)) {
      $obj = $this->decode_encoded_words($obj);
    }
    elseif (is_object($obj)) {
      foreach ($obj as $idx => $val) {
        $obj->$idx = $this->decode_encoded_words_deep($val);
      }
    }

    return $obj;
  }

