getNextUid.code:
function(int $uid)
  {
    if (!$this->stream)
      return null;
    $emails = imap_search($this->stream, 'ALL');

    if ($emails) {
      $emails = array_filter($emails, function($val) use ($uid){
        return $val > $uid;
      });
      if ($emails) {
        return min($emails);
      }
    }

    return null;
  }

