getmboxes.code:
function($dir)
  {
    $mboxes = imap_getmailboxes($this->stream, $this->mbParam, $dir);
    $i      = 0;
    $ret    = array();
    while (list($key, $val) = each($mboxes)) {
      $name      = imap_utf7_decode($val->name);
      $name_arr  = explode('}', $name);
      $j         = count($name_arr) - 1;
      $mbox_name = $name_arr[$j];
      if ($mbox_name == "") {continue; // the DIRECTORY itself
      }

      $ret[$i++] = $mbox_name;
    }

    sort($ret);
    return $ret;
  }

