getdecodevalue.code:
function($message, $coding)
  {
    if ($coding == 0) {
      $message = imap_8bit($message);
    } elseif ($coding == 1) {
      $message = imap_8bit($message);
    } elseif ($coding == 2) {
      $message = imap_binary($message);
    } elseif ($coding == 3) {
      $message = imap_base64($message);
    } elseif ($coding == 4) {
      $message = imap_qprint($message);
    } elseif ($coding == 5) {
      $message = imap_base64($message);
    }

    return $message;
  }

