public static function getOptionsIds(...$codes): array
{
  $codes[] = self::getOptionRoot();
  return array_flip(
    array_filter(
      self::getOptionsObject()->getCodes(...$codes),
      function ($a) {
        return $a !== null;
      }
    )
  );
}

