    $cfg['join'] = [[
      'table' => $t,
      'on' => [
        'conditions' => [[
          'field' => $this->db->cfn('id_media', $t),
          'exp' => $this->db->cfn('id', $cf['tables']['medias'])
        ], [
          'field' => $this->db->cfn('id_group', $t),
          'value' => $idGroup
        ]]
      ]
    ]];
    if (empty($cfg['order']) || !\is_array($cfg['order'])) {
      $cfg['order'] = [];
    }
    $cfg['order'][] = [
      'field' => $this->db->cfn('position', $t),
      'dir' => 'ASC'
    ];
    return $this->browse($cfg, $limit, $start);
  }

