    $f = $cf['arch']['medias_groups_medias'];
    $order = $this->db->selectOne($t, 'position', ['id_group' => $id_group], ['position' => 'DESC']) + 1;
    $res = $this->db->insertIgnore($t, [
      $f['id_group'] => $id_group,
      $f['id_media'] => $id_media,
      $f['position'] => $order ?: null
    ]);
    if ($res && $addTag) {
