getThumbsName.code:
function(string $name, array $size = array (
  0 => 60,
  1 => 60,
))
  {
    if (count($size) === 1) {
      $size[] = $size[0];
    }

    if ((count($size) !== 2) || !Str::isInteger($size[0], $size[1])) {
      return null;
    }

    $ext = Str::fileExt($name, true);
    $dir = dirname($name);
    if (!empty($dir)) {
      $dir .= '/';
    }

    return $dir . $ext[0] . '_w' . $size[0] . 'h' . $size[1] . (empty($ext[1]) ? '' : '.' . $ext[1]);
  }

