public function tree($id, $prepath = false)
{
  if (Str::isUid($id)) {
    if ($this->cacheHas($id, __FUNCTION__)) {
      return $this->cacheGet($id, __FUNCTION__);
    }

    $tree = $this->pref->getTree($id);
    $res  = $this->_arrange($tree, $prepath);
    $this->cacheSet($id, __FUNCTION__, $res['items'] ?? []);
    return $res['items'] ?? [];
  }
}

