function(string $id, $keep = false)
  {
    if (Str::isUid($id)) {
      $cf = &$this->class_cfg;
      if (empty($keep)) {
        $this->removeAllMedias($id);
        $this->removeTags($id);
        foreach ($this->getAliases($id) as $id_alias) {
          $this->remove($id_alias);
        }

        foreach ($this->getChildren($id) as $id_child) {
          $this->remove($id_child);
