urlToId.code:
function($url)
  {
    if (!$url) {
      return null;
    }

    $cf = &$this->class_cfg;
    if (substr($url, 0, 1) !== '/') {
      $url = '/'.$url;
    }

    $res = $this->db->selectOne($cf['tables']['url'], $cf['arch']['id_note'], [$cf['arch']['url']['url'] => $url]);
    return $res ?: null;
  }

