public function read($id, string $id_user = null, $moment = null): bool
{
  if (!$id_user) {
    $id_user = $this->user->getId();
  }

  if (Str::isUid($id_user)) {
    if (\is_array($id)) {
      $todo = count($id);
      $did  = 0;
      foreach ($id as $i) {
        if ($this->read($i, $id_user, $moment)) {
          $did++;
        }
      }

      return $todo === $did;
    }
    elseif (Str::isUid($id)
        && !$this->db->selectOne($this->class_table, $this->fields['read'], [$this->fields['id'] => $id])
    ) {
      return (bool)$this->db->update(
        $this->class_table, [
        $this->fields['read'] => $moment ? \round((float)$moment, 4) : X::microtime()
        ], [
        $this->fields['id'] => $id
        ]
      );
    }
  }

  return false;
}

