public function readAll(string $id_user = null, $moment = null): bool
{
  if (!$id_user) {
    $id_user = $this->user->getId();
  }

  if (Str::isUid($id_user)
      && ($unreads = $this->getUnreadIds($id_user))
  ) {
    $todo = count($unreads);
    $did  = 0;
    foreach ($unreads as $id){
      $did += $this->db->update(
        $this->class_table, [
        $this->fields['read'] => $moment ? \round((float)$moment, 4) : X::microtime()
        ], [
        $this->fields['id'] => $id
        ]
      );
    }

    return $todo === $did;
  }

  return false;
}

