        'where' => [
          'conditions' => [[
              'field' => $parch['id_option'],
              'value' => $id_perm
                ], [
              'logic' => 'OR',
              'conditions' => [[
                'field' => $parch['public'],
                'value' => 1
              ], [
                'field' => $parch['id_user'],
                'value' => $id_user
              ], [
                'field' => $parch['id_group'],
                'value' => $user->{$ucfg['arch']['users']['id_group']}
