  if ($data = $this->dbTraitPrepare($data)) {
    if (!empty($f['cfg'])) {
      $col = $f['cfg'];
      if (!empty($data[$col])) {
        if (is_string($data[$col])) {
          $data[$col] = json_decode($data[$col], true);
