function($id)
  {
    if ( $d = $this->get($id) ){
      $t = new bbn\Util\Timer();
      $t->start();
      $res = $this->_exec($d['request'], $d['params']);
      $duration = (int)ceil($t->stop() * 1000);
      if ( $res !== $d['result'] ){
        $this->db->update('bbn_observers', [
