function($id)
  {
    if ( $this->check() ){
      $d = $this->db->rselect('bbn_observers', [], [
        'id' => $id
      ]);
      if ( !$d['id_alias'] ){
        return $d;
      }
      if ($alias = $this->db->rselect('bbn_observers', [], [
        'id' => $d['id_alias']
      ])) {
        $alias['id'] = $d['id'];
        $alias['result'] = $d['result'];
        $alias['id_alias'] = $d['id_alias'];
        return $alias;
      }
    }
    return null;
  }

