getPathArray.code:
function(string $id, $root = NULL)
  {
    if (!isset($root)) {
      $root = $this->getDefault();
    }

    if ($code = $this->code($id)) {
      $parts = [];
      while ($id && ($id !== $root)){
        array_unshift($parts, $code);
        if (!($id = $this->getIdParent($id))) {
          return null;
        }

        $code = $this->code($id);
      }

      return $parts;
    }

    return null;
  }

fromPath.code:
function(string $path, string $sep = '|', $parent = NULL)
  {
    if ($this->check()) {
      if (!empty($sep)) {
        $parts = explode($sep, $path);
      }
      else{
        $parts = [$path];
      }

      if (null === $parent) {
        $parent = $this->default;
      }

      foreach ($parts as $p){
        if (!($parent = $this->fromCode($p, $parent))) {
          break;
        }
      }

      return $parent ?: null;
    }

    return null;
  }

