public function isSortable(...$codes): ?bool
{
  // Get the ID of the option from its code.
  $id = $this->fromCode($codes);

  // Check if the ID is valid and retrieve the config to check for sortability.
  if (Str::isUid($id)) {
    $cfg = $this->getCfg($id);
    return !empty($cfg['sortable']);
  }

  return null;
}

