private function getTranslatingLocale(string $id): ?string
{
  $originalLocale = $this->findI18nById($id);
  $locale = null;
  if (!empty($originalLocale)
    && \defined('BBN_LANG')
    && (BBN_LANG !== $originalLocale)
  ) {
    $locale = BBN_LANG;
  }

  return $locale;
}

