public function nativeOptions($code = null): ?array
{
  if (Str::isUid($id = $this->fromCode(\func_get_args()))) {
    $res = [];
    if ($its = $this->items($id)) {
      foreach ($its as $it) {
        $res[] = $this->nativeOption($it);
      }
    }

    return $res;
  }

  return null;
}

