options.code:
public function options(...$codes): ?array
{
  if ($id = $this->fromCode(...$codes)) {
    $locale = $this->getTranslatingLocale($id);
    if ($r = $this->getCache($id, __FUNCTION__, $locale)) {
      return $r;
    }

    $cf  =& $this->fields;
    $opts = $this->fullOptions($id) ?: [];
    $res = [];
    foreach ($opts as $o) {
      if (\is_null($o[$cf['text']]) && !empty($o[$cf['id_alias']])) {
        $o[$cf['text']] = $this->text($o[$cf['id_alias']]);
      }
      if (!empty($o[$cf['text']])
        && !empty($locale)
        && ($t = $this->getTranslation($o[$cf['id']], $locale))
      ) {
        $o[$cf['text']] = $t;
      }
      $res[$o[$cf['id']]] = $o[$cf['text']];
    }

    //\asort($res);
    $this->setCache($id, __FUNCTION__, $res, $locale);
    return $res;
  }

  return null;
}

items.code:
public function items(...$code): ?array
{
  if (Str::isUid($id = $this->fromCode(...$code))) {
    if (($res = $this->getCache($id, __FUNCTION__)) !== false) {
      return $res;
    }

    $cfg = $this->getCfg($id) ?: [];
    if ($cfg || $this->dbTraitExists($id)) {
      // If not sortable returning an array ordered by text
      $order = empty($cfg['sortable']) ? [
          $this->fields['text'] => 'ASC',
          $this->fields['code'] => 'ASC',
          $this->fields['id'] => 'ASC',
        ] : [
          $this->fields['num'] => 'ASC',
          $this->fields['text'] => 'ASC',
          $this->fields['code'] => 'ASC',
          $this->fields['id'] => 'ASC',
        ];
      $res   = $this->db->getColumnValues(
        $this->class_cfg['table'],
        $this->fields['id'], [
        $this->fields['id_parent'] => $id,
        [$this->fields['id'], '!=', $id],
        ], $order
      );
      if (!$this->isExporting && empty($res)) {
        $opt = $this->option($id);
        if (!$opt['text'] && $opt['id_alias'] && !$this->isPartOfTemplates($opt['id_alias'])) {
          $res   = $this->db->getColumnValues(
            $this->class_cfg['table'],
            $this->fields['id'], [
            $this->fields['id_parent'] => $opt['id_alias'],
            ], $order
          );
        }
      }

      $this->setCache($id, __FUNCTION__, $res);
      return $res;
    }
  }

  return null;
}

