parents.code:
public function parents($code = null): ?array
{
  if (Str::isUid($id = $this->fromCode(\func_get_args()))) {
    $res = [];
    while (Str::isUid($id_parent = $this->getIdParent($id))){
      if (\in_array($id_parent, $res, true)) {
        break;
      }
      else{
        if ($id === $id_parent) {
          break;
        }
        else{
          $res[] = $id_parent;
          $id    = $id_parent;
        }
      }
    }

    return $res;
  }

  return null;
}

