sequence.code:
public function sequence(string $id_option, string|null $id_root = null): ?array
{
  if ($this->check()) {
    if (null === $id_root) {
      $id_root = $this->getRoot();
    }

    if ($this->exists($id_root) && ($parents = $this->parents($id_option))) {
      $res = [$id_option];
      foreach ($parents as $p){
        array_unshift($res, $p);
        if ($p === $id_root) {
          return $res;
        }
      }
    }
  }

  return null;
}

