isParent.code:
public function isParent($id, $id_parent): bool
{
  // Preventing infinite loop
  $done = [$id];
  if (Str::isUid($id) && Str::isUid($id_parent)) {
    while ($id = $this->getIdParent($id)){
      if ($id === $id_parent) {
        return true;
      }

      if (\in_array($id, $done, true)) {
        break;
      }

      $done[] = $id;
    }
  }

  return false;
}

