public function closest(string $id, string|array $target): ?string
{
  $ids = $this->parents($id);
  if (!\is_array($target)) {
    $target = [Str::isUid($target) ? 'id_alias' : 'code' => $target];
  }

  foreach ($ids as $i) {
    $opt = $this->option($i);
    if (X::getRow([$opt], $target)) {
      return $i;
    }
  }

  return null;
}

getPathArray.code:
public function getPathArray(string $id, $root = null): ?array
{
  if (!isset($root)) {
    $root = $this->getDefault();
  }

  $done = [];
  if ($code = $this->code($id)) {
    $parts = [];
    while ($id && ($id !== $root)){
      array_unshift($parts, $code);
      if (!($tmp = $this->getIdParent($id))) {
        return null;
      }

      if (in_array($tmp, $done, true)) {
        break;
      }
      else{
        $id = $tmp;
        $done[] = $id;
      }

      $code = $this->code($id);
    }

    return $parts;
  }

  return null;
}

